        private void set_initial_form_load()
        {
            m_obj_trans = get_trans_object(m_fg);
            load_data_2_grid();
        }
        private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg)
        {
            Hashtable v_htb = new Hashtable();
            v_htb.Add(RPT_HANG_HOA_LOI_NHUAN.GIA_NHAP, e_col_Number.GIA_NHAP);
            v_htb.Add(RPT_HANG_HOA_LOI_NHUAN.GIA_XUAT, e_col_Number.GIA_XUAT);
            v_htb.Add(RPT_HANG_HOA_LOI_NHUAN.TEN_HANG_VN, e_col_Number.TEN_HANG_VN);
            v_htb.Add(RPT_HANG_HOA_LOI_NHUAN.MA_HANG, e_col_Number.MA_HANG);

            ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg, v_htb, m_ds.RPT_HANG_HOA_LOI_NHUAN.NewRow());
            return v_obj_trans;
        }
        private void load_data_2_grid()
        {
            m_ds = new DS_RPT_HANG_HOA_LOI_NHUAN();
            m_us.FillDataset_By_Id(m_ds, m_id_nhom_hang, m_tu_ngay, m_den_ngay);
            //1. Dua lieu len luoi
            m_fg.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            
            //  2. Tao cot loi nhuan 
            for (int v_i_cur_row = m_fg.Rows.Fixed; v_i_cur_row < m_fg.Rows.Count; v_i_cur_row++)
            {
                if (m_fg[v_i_cur_row, (int)e_col_Number.GIA_XUAT] == null) continue;
                if (m_fg[v_i_cur_row, (int)e_col_Number.GIA_NHAP] == null) continue;
                if (!CIPConvert.is_valid_number(m_fg[v_i_cur_row, (int)e_col_Number.GIA_XUAT])) continue;
                if (!CIPConvert.is_valid_number(m_fg[v_i_cur_row, (int)e_col_Number.GIA_NHAP])) continue;

                m_fg[v_i_cur_row, (int)e_col_Number.LOI_NHUAN]
                    = CIPConvert.ToDecimal( m_fg[v_i_cur_row, (int)e_col_Number.GIA_XUAT])
                    - CIPConvert.ToDecimal (m_fg[v_i_cur_row, (int)e_col_Number.GIA_NHAP]);
            }

            //  3. Tao group 
            m_fg.Subtotal(
                AggregateEnum.Sum
                , 0
                , -1
                , (int)e_col_Number.GIA_NHAP
                , "Tổng");

            m_fg.Subtotal(AggregateEnum.Sum
                , 0
                , -1
                , (int)e_col_Number.GIA_XUAT
                , "Tổng");
            m_fg.Redraw = true;

            m_fg.Subtotal(AggregateEnum.Sum
              , 0
              , -1
              , (int)e_col_Number.LOI_NHUAN
              , "Tổng");
            m_fg.Redraw = true;
        }
        private void grid2us_object(US_RPT_HANG_HOA_LOI_NHUAN i_us
            , int i_grid_row)
        {
            DataRow v_dr;
            v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            m_obj_trans.GridRow2DataRow(i_grid_row, v_dr);
            i_us.DataRow2Me(v_dr);
        }


        private void us_object2grid(US_RPT_HANG_HOA_LOI_NHUAN i_us
            , int i_grid_row)
        {
            DataRow v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            i_us.Me2DataRow(v_dr);
            m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
        }


