		#region Data Structure
		private enum e_col_Number{
			GIA_BAN = 6
,MA_HANG = 1
,TEN_HANG_VN = 2
,TEN_HANG_EN = 4
,MO_TA = 8
,DON_VI = 3
,GIA_BAN_LE = 7
,GIA_NHAP = 5

		}			
