        private void m_fg_Click(object sender, EventArgs e)
        {
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
            grid2us_object(m_us, m_fg.Row);

            m_lbl_thong_tin.Text = m_us.strTEN_HANG_VN;
            m_lbl_4.Text = m_us.strMA_HANG;
            m_txt_ma_hang_hoa.Text = m_us.strMA_HANG;
            m_txt_ten_hang_hoa.Text = m_us.strTEN_HANG_VN;
            m_txt_don_vi.Text = m_us.strDON_VI;
            if (m_us.dcGIA_BAN != null)
            {
                m_txt_gia_ban.Text = CIPConvert.ToStr(m_us.dcGIA_BAN);
            }
            else
            {
                m_txt_gia_ban.Text = "";
            }
            if (m_us.dcGIA_NHAP != null)
            {
                m_txt_gia_nhap.Text = CIPConvert.ToStr(m_us.dcGIA_NHAP);
            }
            else
            {
                m_txt_gia_nhap.Text = "";
            }
            if (m_us.dcGIA_BAN_LE != null)
            {
                m_txt_gia_ban_le.Text = CIPConvert.ToStr(m_us.dcGIA_BAN_LE);
            }
            else
            {
                m_txt_gia_ban_le.Text = "";
            }
            m_txt_mo_ta.Text = m_us.strMO_TA;

        }
