			grid2us_object(m_us, m_fg.Row);
			this.m_lbl_ma_kho_de.Text = m_us.strMA_KHO;
			this.m_txt_ma_nv.Text = m_us.dcID_NHAN_VIEN.ToString();
			this.m_txt_dia_chi.Text = m_us.strDIA_CHI;
			this.m_txt_ghi_chu.Text = m_us.strGHI_CHU;
			this.m_txt_so_dien_thoai.Text = m_us.strDIEN_THOAI;
		}
		private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg)
		{
			Hashtable v_htb = new Hashtable();
			v_htb.Add(DM_KHO.GHI_CHU, e_col_Number.GHI_CHU);
			v_htb.Add(DM_KHO.DIA_CHI, e_col_Number.DIA_CHI);
			v_htb.Add(DM_KHO.MA_KHO, e_col_Number.MA_KHO);
			v_htb.Add(DM_KHO.TEN_KHO, e_col_Number.TEN_KHO);
			v_htb.Add(DM_KHO.ID_NHAN_VIEN, e_col_Number.ID_NHAN_VIEN);
			v_htb.Add(DM_KHO.DIEN_THOAI, e_col_Number.DIEN_THOAI);

			ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg, v_htb, m_ds.DM_KHO.NewRow());
			return v_obj_trans;
		}
		private void load_data_2_grid()
		{
			m_ds = new DS_DM_KHO();
			m_us.FillDataset(m_ds);
			m_fg.Redraw = false;
			CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
			m_fg.Redraw = true;
		}
		private void grid2us_object(US_DM_KHO i_us
			, int i_grid_row)
		{
			DataRow v_dr;
			v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
			m_obj_trans.GridRow2DataRow(i_grid_row, v_dr);
			i_us.DataRow2Me(v_dr);
		}


		private void us_object2grid(US_DM_KHO i_us
			, int i_grid_row)
		{
			DataRow v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
			i_us.Me2DataRow(v_dr);
			m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
		}
		private void insert_dm_kho()
		{
			f501_DM_KHO_DE v_fDE = new f501_DM_KHO_DE();							
			v_fDE.display_for_insert();
			load_data_2_grid();
		}

		private void update_dm_kho()
		{
			if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
			if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
			grid2us_object(m_us, m_fg.Row);
			f501_DM_KHO_DE v_fDE = new f501_DM_KHO_DE();
			v_fDE.display_for_update(m_us);
			load_data_2_grid();
		}

		private void delete_dm_kho()
		{
			if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
			if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
			if (BaseMessages.askUser_DataCouldBeDeleted(8) != BaseMessages.IsDataCouldBeDeleted.CouldBeDeleted) return;
			US_DM_KHO v_us = new US_DM_KHO();
			grid2us_object(v_us, m_fg.Row);
			try
			{
				v_us.BeginTransaction();
				v_us.Delete();
				v_us.CommitTransaction();
				m_fg.Rows.Remove(m_fg.Row);
			}
			catch (Exception v_e)
			{
				v_us.Rollback();
				CDBExceptionHandler v_objErrHandler = new CDBExceptionHandler(v_e,
					new CDBClientDBExceptionInterpret());
				v_objErrHandler.showErrorMessage();
			}
		}

		private void view_dm_kho()
		{
			if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
			if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
			grid2us_object(m_us, m_fg.Row);
			//	f501_DM_KHO_DE v_fDE = new f501_DM_KHO_DE();			
			//	v_fDE.display(m_us);
		}
