			this.m_txt_dia_chi.Text = m_us.strDIA_CHI;
			this.m_txt_ghi_chu.Text = m_us.strGHI_CHU;
			this.m_txt_so_dien_thoai.Text = m_us.strDIEN_THOAI;
		}
