		private void load_data_2_grid(){						
			m_ds = new DS_DM_NHA_CUNG_CAP();			
			m_us.FillDataset(m_ds);
			m_dgv.Redraw = false;
			CGridUtils.Dataset2C1Grid(m_ds, m_dgv, m_obj_trans);
			m_dgv.Redraw = true;
		}
		private void grid2us_object(US_DM_NHA_CUNG_CAP i_us
			, int i_grid_row) {
			DataRow v_dr;
			v_dr = (DataRow) m_dgv.Rows[i_grid_row].UserData;
			m_obj_trans.GridRow2DataRow(i_grid_row,v_dr);
			i_us.DataRow2Me(v_dr);
		}

	
		private void us_object2grid(US_DM_NHA_CUNG_CAP i_us
			, int i_grid_row) {
			DataRow v_dr = (DataRow) m_dgv.Rows[i_grid_row].UserData;
			i_us.Me2DataRow(v_dr);
			m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
		}


		private void insert_dm_nha_cung_cap(){
            f801_DM_NHA_CUNG_CAP_DE v_fDE = new f801_DM_NHA_CUNG_CAP_DE();								
			v_fDE.display_for_insert();
			load_data_2_grid();
		}

		private void update_dm_nha_cung_cap(){			
			if (!CGridUtils.IsThere_Any_NonFixed_Row(m_dgv)) return;
			if (!CGridUtils.isValid_NonFixed_RowIndex(m_dgv, m_dgv.Row)) return;			
			grid2us_object(m_us, m_dgv.Row);
			f801_DM_NHA_CUNG_CAP_DE v_fDE = new f801_DM_NHA_CUNG_CAP_DE();
			v_fDE.display_for_update(m_us);
			load_data_2_grid();
		}
				
		private void delete_dm_nha_cung_cap(){
			if (!CGridUtils.IsThere_Any_NonFixed_Row(m_dgv)) return;
			if (!CGridUtils.isValid_NonFixed_RowIndex(m_dgv, m_dgv.Row)) return;
			if (BaseMessages.askUser_DataCouldBeDeleted(8) != BaseMessages.IsDataCouldBeDeleted.CouldBeDeleted)  return;
			US_DM_NHA_CUNG_CAP v_us = new US_DM_NHA_CUNG_CAP();
			grid2us_object(v_us, m_dgv.Row);
			try {			
				v_us.BeginTransaction();    											
				v_us.Delete();                      								
				v_us.CommitTransaction();
				m_dgv.Rows.Remove(m_dgv.Row);				
			}
			catch (Exception v_e) {
				v_us.Rollback();
				CDBExceptionHandler v_objErrHandler = new CDBExceptionHandler(v_e,
					new CDBClientDBExceptionInterpret());
				v_objErrHandler.showErrorMessage();
			}
		}

